import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable, of } from 'rxjs';
import { catchError, map } from 'rxjs/operators';
import { environment } from '../../environments/environment';
import { Vehicle } from '../models/vehicle.model';

@Injectable({
  providedIn: 'root'
})
export class VehicleService {
  private apiUrl = environment.apiUrl + '/vehicles';

  constructor(private http: HttpClient) {}

  getVehicles(): Observable<Vehicle[]> {
    return this.http.get<Vehicle[]>(this.apiUrl).pipe(
      catchError(error => {
        console.error('Vehicle fetch error', error);
        // Hata durumunda mock veriler dönebiliriz
        return of([
          { plate: '34 ABC 123', model: 'Mercedes Sprinter', status: 'hazır' },
          { plate: '06 DEF 456', model: 'Ford Transit', status: 'görevde' },
          { plate: '35 GHI 789', model: 'Volkswagen Crafter', status: 'uzun yolda' }
        ] as Vehicle[]);
      })
    );
  }

  getVehicleStats(): Observable<{
    total: number;
    ready: number;
    onDuty: number;
    longRoute: number;
    standby: number;
  }> {
    return this.http.get<any>(this.apiUrl + '/stats').pipe(
      catchError(error => {
        console.error('Vehicle stats fetch error', error);
        // Hata durumunda varsayılan istatistikler
        return of({
          total: 50,
          ready: 20,
          onDuty: 15,
          longRoute: 10,
          standby: 5
        });
      })
    );
  }
}
