import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { ReactiveFormsModule } from '@angular/forms';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { AuthService } from '../../services/auth.service';

@Component({
  selector: 'app-kayit',
  templateUrl: './kayit.component.html',
  styleUrls: ['./kayit.component.css'],
  standalone: true,
  imports: [CommonModule, ReactiveFormsModule]
})
export class KayitComponent implements OnInit {
  kayitForm: FormGroup;
  kayitError: string = '';
  isLoading: boolean = false;

  constructor(
    private fb: FormBuilder,
    private authService: AuthService,
    private router: Router
  ) {
    this.kayitForm = this.fb.group({
      username: ['', [Validators.required, Validators.minLength(3)]],
      ad: ['', [Validators.required]],
      soyad: ['', [Validators.required]],
      password: ['', [Validators.required, Validators.minLength(6)]],
      role: ['user', [Validators.required]]
    });
  }

  ngOnInit(): void {}

  onSubmit() {
    if (this.kayitForm.valid) {
      this.isLoading = true;
      this.kayitError = '';

      const userData = this.kayitForm.value;

      // Kayıt işlemi için backend metodu eklenecek
      // Şimdilik console log ile gösterelim
      console.log('Kayıt verisi:', userData);

      this.isLoading = false;
      this.router.navigate(['/giris']);
    }
  }

  // Form kontrol metodları
  get username() { return this.kayitForm.get('username'); }
  get ad() { return this.kayitForm.get('ad'); }
  get soyad() { return this.kayitForm.get('soyad'); }
  get password() { return this.kayitForm.get('password'); }
  get role() { return this.kayitForm.get('role'); }
}
<div class="kayit-container">
  <div class="kayit-card">
    <img src="assets/logo.png" alt="Araç Sevk Logo" class="logo">
    <h2>Kayıt Ol</h2>

    <form [formGroup]="kayitForm" (ngSubmit)="onSubmit()" class="kayit-form">
      <div class="form-group">
        <label for="username">Kullanıcı Adı</label>
        <input 
          type="text" 
          id="username" 
          formControlName="username" 
          placeholder="Kullanıcı adınızı girin"
          [ngClass]="{'is-invalid': username?.invalid && (username?.dirty || username?.touched)}"
        >
        <div *ngIf="username?.invalid && (username?.dirty || username?.touched)" class="invalid-feedback">
          <small *ngIf="username?.errors?.['required']">Kullanıcı adı zorunludur</small>
          <small *ngIf="username?.errors?.['minlength']">En az 3 karakter olmalıdır</small>
        </div>
      </div>

      <div class="form-group">
        <label for="ad">Ad</label>
        <input 
          type="text" 
          id="ad" 
          formControlName="ad" 
          placeholder="Adınızı girin"
          [ngClass]="{'is-invalid': ad?.invalid && (ad?.dirty || ad?.touched)}"
        >
        <div *ngIf="ad?.invalid && (ad?.dirty || ad?.touched)" class="invalid-feedback">
          <small *ngIf="ad?.errors?.['required']">Ad zorunludur</small>
        </div>
      </div>

      <div class="form-group">
        <label for="soyad">Soyad</label>
        <input 
          type="text" 
          id="soyad" 
          formControlName="soyad" 
          placeholder="Soyadınızı girin"
          [ngClass]="{'is-invalid': soyad?.invalid && (soyad?.dirty || soyad?.touched)}"
        >
        <div *ngIf="soyad?.invalid && (soyad?.dirty || soyad?.touched)" class="invalid-feedback">
          <small *ngIf="soyad?.errors?.['required']">Soyad zorunludur</small>
        </div>
      </div>

      <div class="form-group">
        <label for="password">Şifre</label>
        <input 
          type="password" 
          id="password" 
          formControlName="password" 
          placeholder="Şifrenizi girin"
          [ngClass]="{'is-invalid': password?.invalid && (password?.dirty || password?.touched)}"
        >
        <div *ngIf="password?.invalid && (password?.dirty || password?.touched)" class="invalid-feedback">
          <small *ngIf="password?.errors?.['required']">Şifre zorunludur</small>
          <small *ngIf="password?.errors?.['minlength']">Şifre en az 6 karakter olmalıdır</small>
        </div>
      </div>

      <div class="form-group">
        <label for="role">Kullanıcı Rolü</label>
        <select 
          id="role" 
          formControlName="role"
          [ngClass]="{'is-invalid': role?.invalid && (role?.dirty || role?.touched)}"
        >
          <option value="user">Kullanıcı</option>
          <option value="admin">Yönetici</option>
          <option value="driver">Sürücü</option>
        </select>
        <div *ngIf="role?.invalid && (role?.dirty || role?.touched)" class="invalid-feedback">
          <small *ngIf="role?.errors?.['required']">Rol seçimi zorunludur</small>
        </div>
      </div>

      <div *ngIf="kayitError" class="alert alert-danger">
        {{ kayitError }}
      </div>

      <button 
        type="submit" 
        class="btn btn-primary" 
        [disabled]="kayitForm.invalid || isLoading"
      >
        {{ isLoading ? 'Kayıt Yapılıyor...' : 'Kayıt Ol' }}
      </button>
    </form>

    <div class="kayit-footer">
      <p class="login-link">Zaten hesabınız var mı? <a routerLink="/giris">Giriş Yap</a></p>
    </div>
  </div>
</div>

