import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { RouterModule, Router } from '@angular/router';
import { AuthService, User } from '../../services/auth.service';

interface Vehicle {
  plate: string;
  model: string;
  status: 'hazır' | 'görevde' | 'uzun yolda' | 'kademede';
}

@Component({
  selector: 'app-dashboard',
  templateUrl: './dashboard.component.html',
  standalone: true,
  imports: [CommonModule, RouterModule]
})
export class DashboardComponent implements OnInit {
  currentUser: User | null = null;
  vehicleStats = {
    total: 50,
    ready: 20,
    onDuty: 15,
    longRoute: 10,
    standby: 5
  };

  vehicles: Vehicle[] = [
    { plate: '34 ABC 123', model: 'Mercedes Sprinter', status: 'hazır' },
    { plate: '06 DEF 456', model: 'Ford Transit', status: 'görevde' },
    { plate: '35 GHI 789', model: 'Volkswagen Crafter', status: 'uzun yolda' },
    { plate: '16 JKL 012', model: 'Fiat Ducato', status: 'kademede' },
    { plate: '07 MNO 345', model: 'Renault Master', status: 'hazır' },
    { plate: '41 PRS 678', model: 'Iveco Daily', status: 'görevde' },
    { plate: '33 TUV 901', model: 'Mercedes Vito', status: 'uzun yolda' },
    { plate: '55 WXY 234', model: 'Peugeot Boxer', status: 'kademede' }
  ];

  constructor(
    private router: Router,
    private authService: AuthService
  ) {}

  ngOnInit() {
    // Örnek kullanıcı verisi
    const exampleUser: User = {
      id: 1,
      username: 'admin',
      ad: 'Ahmet',
      soyad: 'Yılmaz',
      token: 'sample-token'
    };
    this.currentUser = exampleUser;

    this.authService.currentUser.subscribe((user: User | null) => {
      this.currentUser = user || exampleUser;
    });
    // Gerçek veriler için servis çağrısı yapılacak
  }

  logout() {
    this.authService.logout();
    this.router.navigate(['/giris']);
  }

  getVehicleStatusClass(status: string): string {
    switch(status) {
      case 'hazır': return 'ready';
      case 'görevde': return 'on-duty';
      case 'uzun yolda': return 'long-route';
      case 'kademede': return 'standby';
      default: return '';
    }
  }
}
<div class="dashboard-container">
  <nav class="navbar navbar-expand-lg navbar-dark bg-primary">
    <div class="container-fluid">
      <a class="navbar-brand" href="#">Araç Sevk Sistemi</a>
      <button class="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarNav">
        <span class="navbar-toggler-icon"></span>
      </button>
      <div class="collapse navbar-collapse" id="navbarNav">
        <ul class="navbar-nav me-auto">
          <li class="nav-item">
            <a class="nav-link" routerLink="/drivers" routerLinkActive="active">Şoförler</a>
          </li>
          <li class="nav-item">
            <a class="nav-link" routerLink="/vehicles" routerLinkActive="active">Araçlar</a>
          </li>
          <li class="nav-item">
            <a class="nav-link" routerLink="/tasks" routerLinkActive="active">Görevler</a>
          </li>
        </ul>
        <div class="d-flex">
          <span class="navbar-text me-3">
            Hoş geldiniz, {{ currentUser?.ad }} {{ currentUser?.soyad }}
          </span>
          <button class="btn btn-outline-light" (click)="logout()">Çıkış</button>
        </div>
      </div>
    </div>
  </nav>

  <div class="content p-4">
    <router-outlet></router-outlet>
  </div>
</div>
