import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { BehaviorSubject, Observable } from 'rxjs';
import { map } from 'rxjs/operators';

export interface User {
  id: number;
  username: string;
  ad: string;
  soyad: string;
  role: 'admin' | 'user' | 'driver';
  token?: string;
}

@Injectable({
  providedIn: 'root'
})
export class AuthService {
  private currentUserSubject: BehaviorSubject<User | null>;
  public currentUser: Observable<User | null>;

  constructor(private http: HttpClient) {
    const storedUser = localStorage.getItem('currentUser');
    this.currentUserSubject = new BehaviorSubject<User | null>(
      storedUser ? JSON.parse(storedUser) : null
    );
    this.currentUser = this.currentUserSubject.asObservable();
  }

  public get currentUserValue(): User | null {
    return this.currentUserSubject.value;
  }

  login(username: string, password: string): Observable<User> {
    return this.http.post<User>('/api/auth/login', { username, password }).pipe(
      map(user => {
        // Login başarılı ise
        if (user && user.token) {
          // Kullanıcı bilgilerini local storage'a kaydet
          localStorage.setItem('currentUser', JSON.stringify(user));

          // BehaviorSubject'i güncelle
          this.currentUserSubject.next(user);
        }
        return user;
      })
    );
  }

  logout() {
    // Local storage'dan kullanıcı bilgilerini sil
    localStorage.removeItem('currentUser');

    // BehaviorSubject'i null yap
    this.currentUserSubject.next(null);
  }

  isLoggedIn(): boolean {
    return !!this.currentUserValue;
  }

  getUserRole(): 'admin' | 'user' | 'driver' | null {
    return this.currentUserValue?.role || null;
  }
}
