import { Injectable } from '@angular/core';
import { HttpClient, HttpHeaders } from '@angular/common/http';
import { Observable, of } from 'rxjs';
import { catchError, map, tap } from 'rxjs/operators';
import { environment } from '../../environments/environment';
import { Vehicle } from '../models/vehicle.model';
import { AuthService } from './auth.service';

export interface VehicleStats {
  total: number;
  ready: number;
  onDuty: number;
  longRoute: number;
  standby: number;
}

interface RawVehicle {
  plate: string;
  model: string;
  status: string;
}

@Injectable({
  providedIn: 'root'
})
export class VehicleService {
  private apiUrl = environment.apiUrl + '/vehicles';

  constructor(
    private http: HttpClient,
    private authService: AuthService
  ) {}

  private getHeaders(): HttpHeaders {
    const token = this.authService.getToken();
    console.log('Token for vehicle request:', token);
    return new HttpHeaders({
      'Content-Type': 'application/json',
      'Authorization': `Bearer ${token}`
    });
  }

  private mapToVehicle(rawVehicle: RawVehicle): Vehicle {
    console.log('Mapping raw vehicle:', rawVehicle);
    return {
      plate: rawVehicle.plate,
      model: rawVehicle.model,
      status: rawVehicle.status === 'hazır' ? 'hazır' : 
              rawVehicle.status === 'görevde' ? 'görevde' : 
              rawVehicle.status === 'uzun yolda' ? 'uzun yolda' : 
              rawVehicle.status === 'kademede' ? 'kademede' :
              'hazır'
    };
  }

  getVehicles(): Observable<Vehicle[]> {
    console.log('Fetching vehicles from:', this.apiUrl);
    return this.http.get<RawVehicle[]>(this.apiUrl, { 
      headers: this.getHeaders() 
    }).pipe(
      tap(vehicles => console.log('Raw vehicles:', vehicles)),
      map(vehicles => vehicles.map(this.mapToVehicle)),
      tap(vehicles => console.log('Mapped vehicles:', vehicles)),
      catchError(error => {
        console.error('Vehicle fetch error:', error);
        console.error('Error details:', {
          status: error.status,
          message: error.message,
          url: this.apiUrl
        });
        // Mock veriler
        return of([
          { plate: '34ABC123', model: 'Mercedes Sprinter', status: 'hazır' },
          { plate: '34XYZ789', model: 'Ford Transit', status: 'görevde' }
        ] as Vehicle[]);
      })
    );
  }

  getVehicleStats(): Observable<VehicleStats> {
    console.log('Fetching vehicle stats from:', this.apiUrl + '/stats');
    return this.http.get<VehicleStats>(this.apiUrl + '/stats', { 
      headers: this.getHeaders() 
    }).pipe(
      tap(stats => console.log('Vehicle stats:', stats)),
      catchError(error => {
        console.error('Vehicle stats error:', error);
        // Mock istatistikler
        return of({
          total: 2,
          ready: 1,
          onDuty: 1,
          longRoute: 0,
          standby: 0
        });
      })
    );
  }
}
